INSERT INTO `wp_options` VALUES (41438,'_site_transient_wp_remote_block_patterns_7681596337af606bc87c55e6d436ea14','a:26:{i:0;O:8:\"stdClass\":7:{s:2:\"id\";i:1244;s:5:\"title\";O:8:\"stdClass\":1:{s:8:\"rendered\";s:34:\"2つ並べて表示された画像\";}s:7:\"content\";O:8:\"stdClass\":2:{s:8:\"rendered\";s:847:\"\n<figure class=\"wp-container-1 wp-block-gallery-62725277b7e09 wp-block-gallery alignwide columns-2 is-cropped\"><ul class=\"blocks-gallery-grid\"><li class=\"blocks-gallery-item\"><figure><img src=\"https://s.w.org/images/core/5.8/nature-above-01.jpg\" alt=\"海岸に打ち寄せる波の空中写真。\" data-full-url=\"https://s.w.org/images/core/5.8/nature-above-01.jpg\" data-link=\"#\" /></figure></li><li class=\"blocks-gallery-item\"><figure><img src=\"https://s.w.org/images/core/5.8/nature-above-02.jpg\" alt=\"地面の空中写真。右上の角には道路が通っている。\" data-full-url=\"https://s.w.org/images/core/5.8/nature-above-02.jpg\" data-link=\"#\" /></figure></li></ul></figure>\n<style>.wp-container-1 {display: flex;gap: var( --wp--style--block-gap, 0.5em );flex-wrap: wrap;align-items: center;}.wp-container-1 > * { margin: 0; }</style>\";s:9:\"protected\";b:0;}s:4:\"meta\";O:8:\"stdClass\":6:{s:10:\"spay_email\";s:0:\"\";s:13:\"wpop_keywords\";s:0:\"\";s:16:\"wpop_description\";s:43:\"画像2枚を含む画像ギャラリー。\";s:19:\"wpop_viewport_width\";i:800;s:16:\"wpop_block_types\";a:0:{}s:11:\"wpop_locale\";s:2:\"ja\";}s:14:\"category_slugs\";a:1:{i:0;s:7:\"gallery\";}s:13:\"keyword_slugs\";a:1:{i:0;s:4:\"core\";}s:15:\"pattern_content\";s:732:\"<!-- wp:gallery {\"ids\":[null,null],\"linkTo\":\"none\",\"align\":\"wide\"} -->\n<figure class=\"wp-block-gallery alignwide columns-2 is-cropped\"><ul class=\"blocks-gallery-grid\"><li class=\"blocks-gallery-item\"><figure><img src=\"https://s.w.org/images/core/5.8/nature-above-01.jpg\" alt=\"海岸に打ち寄せる波の空中写真。\" data-full-url=\"https://s.w.org/images/core/5.8/nature-above-01.jpg\" data-link=\"#\" /></figure></li><li class=\"blocks-gallery-item\"><figure><img src=\"https://s.w.org/images/core/5.8/nature-above-02.jpg\" alt=\"地面の空中写真。右上の角には道路が通っている。\" data-full-url=\"https://s.w.org/images/core/5.8/nature-above-02.jpg\" data-link=\"#\" /></figure></li></ul></figure>\n<!-- /wp:gallery -->\";}i:1;O:8:\"stdClass\":7:{s:2:\"id\";i:1228;s:5:\"title\";O:8:\"stdClass\":1:{s:8:\"rendered\";s:52:\"2カラムのテキストとオフセット見出し\";}s:7:\"content\";O:8:\"stdClass\":2:{s:8:\"rendered\";s:5168:\"\n<div class=\"wp-container-9 wp-block-group alignfull has-background\" style=\"background-color:#f2f0e9\">\n<div style=\"height:70px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n\n\n\n<div class=\"wp-container-4 wp-block-columns alignwide are-vertically-aligned-center\">\n<div class=\"wp-container-2 wp-block-column\" style=\"flex-basis:50%\">\n<p class=\"has-text-color\" style=\"color:#000000;font-size:30px;line-height:1.1\"><strong>海からのインスピレーション</strong></p>\n</div>\n<style>.wp-container-2 > .alignleft { float: left; margin-inline-start: 0; margin-inline-end: 2em; }.wp-container-2 > .alignright { float: right; margin-inline-start: 2em; margin-inline-end: 0; }.wp-container-2 > .aligncenter { margin-left: auto !important; margin-right: auto !important; }.wp-container-2 > * { margin-block-start: 0; margin-block-end: 0; }.wp-container-2 > * + * { margin-block-start: var( --wp--style--block-gap ); margin-block-end: 0; }</style>\n\n\n<div class=\"wp-container-3 wp-block-column\" style=\"flex-basis:50%\">\n<hr class=\"wp-block-separator has-text-color has-background is-style-wide\" style=\"background-color:#000000;color:#000000\" />\n</div>\n<style>.wp-container-3 > .alignleft { float: left; margin-inline-start: 0; margin-inline-end: 2em; }.wp-container-3 > .alignright { float: right; margin-inline-start: 2em; margin-inline-end: 0; }.wp-container-3 > .aligncenter { margin-left: auto !important; margin-right: auto !important; }.wp-container-3 > * { margin-block-start: 0; margin-block-end: 0; }.wp-container-3 > * + * { margin-block-start: var( --wp--style--block-gap ); margin-block-end: 0; }</style></div>\n<style>.wp-container-4 {display: flex;gap: var( --wp--style--block-gap, 0.5em );flex-wrap: nowrap;align-items: center;}.wp-container-4 > * { margin: 0; }</style>\n\n\n<div class=\"wp-container-8 wp-block-columns alignwide\">\n<div class=\"wp-container-5 wp-block-column\"></div>\n<style>.wp-container-5 > .alignleft { float: left; margin-inline-start: 0; margin-inline-end: 2em; }.wp-container-5 > .alignright { float: right; margin-inline-start: 2em; margin-inline-end: 0; }.wp-container-5 > .aligncenter { margin-left: auto !important; margin-right: auto !important; }.wp-container-5 > * { margin-block-start: 0; margin-block-end: 0; }.wp-container-5 > * + * { margin-block-start: var( --wp--style--block-gap ); margin-block-end: 0; }</style>\n\n\n<div class=\"wp-container-6 wp-block-column\">\n<p class=\"has-text-color has-extra-small-font-size\" style=\"color:#000000\">ベールを頭に巻きつけて、女性たちは甲板に出た。停泊中の船の暗い姿を通り過ぎながら、川をずんずん下っていくと、ロンドンは淡い黄色の垂れ下がった天蓋に覆われた光の群れになっていた。大劇場の灯り、長い通りの灯り、心地よい家庭を思い起こさせる灯り、空中高く浮かぶ灯りがあった。</p>\n</div>\n<style>.wp-container-6 > .alignleft { float: left; margin-inline-start: 0; margin-inline-end: 2em; }.wp-container-6 > .alignright { float: right; margin-inline-start: 2em; margin-inline-end: 0; }.wp-container-6 > .aligncenter { margin-left: auto !important; margin-right: auto !important; }.wp-container-6 > * { margin-block-start: 0; margin-block-end: 0; }.wp-container-6 > * + * { margin-block-start: var( --wp--style--block-gap ); margin-block-end: 0; }</style>\n\n\n<div class=\"wp-container-7 wp-block-column\">\n<p class=\"has-text-color has-extra-small-font-size\" style=\"color:#000000\">何百年もの間、これらの灯りの上には暗闇が訪れることはなかった。町が同じ場所で永遠に燃え上がるのは恐ろしいことに見えた。少なくとも海へ冒険に出ようとする人々にとっては、町が永遠に焼け、永遠に傷ついた周りを囲む丘に見えることは恐ろしいことだった。船の甲板から見ると、大都会はうずくまって臆病な姿をした、あまり動かない守銭奴のようだった。</p>\n</div>\n<style>.wp-container-7 > .alignleft { float: left; margin-inline-start: 0; margin-inline-end: 2em; }.wp-container-7 > .alignright { float: right; margin-inline-start: 2em; margin-inline-end: 0; }.wp-container-7 > .aligncenter { margin-left: auto !important; margin-right: auto !important; }.wp-container-7 > * { margin-block-start: 0; margin-block-end: 0; }.wp-container-7 > * + * { margin-block-start: var( --wp--style--block-gap ); margin-block-end: 0; }</style></div>\n<style>.wp-container-8 {display: flex;gap: var( --wp--style--block-gap, 0.5em );flex-wrap: nowrap;align-items: center;}.wp-container-8 > * { margin: 0; }</style>\n\n\n<div style=\"height:40px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n</div>\n<style>.wp-container-9 > .alignleft { float: left; margin-inline-start: 0; margin-inline-end: 2em; }.wp-container-9 > .alignright { float: right; margin-inline-start: 2em; margin-inline-end: 0; }.wp-container-9 > .aligncenter { margin-left: auto !important; margin-right: auto !important; }.wp-container-9 > * { margin-block-start: 0; margin-block-end: 0; }.wp-container-9 > * + * { margin-block-start: var( --wp--style--block-gap ); margin-block-end: 0; }</style>\";s:9:\"protected\";b:0;}s:4:\"meta\";O:8:\"stdClass\":6:{s:10:\"spay_email\";s:0:\"\";s:13:\"wpop_keywords\";s:0:\"\";s:16:\"wpop_description\";s:55:\"2カラムのテキストとオフセット見出し。\";s:19:\"wpop_viewport_width\";i:1200;s:16:\"wpop_block_types\";a:0:{}s:11:\"wpop_locale\";s:2:\"ja\";}s:14:\"category_slugs\";a:2:{i:0;s:7:\"columns\";i:1;s:4:\"text\";}s:13:\"keyword_slugs\";a:1:{i:0;s:4:\"core\";}s:15:\"pattern_content\";s:2942:\"<!-- wp:group {\"align\":\"full\",\"style\":{\"color\":{\"background\":\"#f2f0e9\"}}} -->\n<div class=\"wp-block-group alignfull has-background\" style=\"background-color:#f2f0e9\"><!-- wp:spacer {\"height\":70} -->\n<div style=\"height:70px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:columns {\"verticalAlignment\":\"center\",\"align\":\"wide\"} -->\n<div class=\"wp-block-columns alignwide are-vertically-aligned-center\"><!-- wp:column {\"width\":\"50%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:50%\"><!-- wp:paragraph {\"style\":{\"typography\":{\"lineHeight\":\"1.1\",\"fontSize\":\"30px\"},\"color\":{\"text\":\"#000000\"}}} -->\n<p class=\"has-text-color\" style=\"color:#000000;font-size:30px;line-height:1.1\"><strong>海からのインスピレーション</strong></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":\"50%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:50%\"><!-- wp:separator {\"customColor\":\"#000000\",\"className\":\"is-style-wide\"} -->\n<hr class=\"wp-block-separator has-text-color has-background is-style-wide\" style=\"background-color:#000000;color:#000000\" />\n<!-- /wp:separator --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns {\"align\":\"wide\"} -->\n<div class=\"wp-block-columns alignwide\"><!-- wp:column -->\n<div class=\"wp-block-column\"></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"style\":{\"color\":{\"text\":\"#000000\"}},\"fontSize\":\"extra-small\"} -->\n<p class=\"has-text-color has-extra-small-font-size\" style=\"color:#000000\">ベールを頭に巻きつけて、女性たちは甲板に出た。停泊中の船の暗い姿を通り過ぎながら、川をずんずん下っていくと、ロンドンは淡い黄色の垂れ下がった天蓋に覆われた光の群れになっていた。大劇場の灯り、長い通りの灯り、心地よい家庭を思い起こさせる灯り、空中高く浮かぶ灯りがあった。</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"style\":{\"color\":{\"text\":\"#000000\"}},\"fontSize\":\"extra-small\"} -->\n<p class=\"has-text-color has-extra-small-font-size\" style=\"color:#000000\">何百年もの間、これらの灯りの上には暗闇が訪れることはなかった。町が同じ場所で永遠に燃え上がるのは恐ろしいことに見えた。少なくとも海へ冒険に出ようとする人々にとっては、町が永遠に焼け、永遠に傷ついた周りを囲む丘に見えることは恐ろしいことだった。船の甲板から見ると、大都会はうずくまって臆病な姿をした、あまり動かない守銭奴のようだった。</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:spacer {\"height\":40} -->\n<div style=\"height:40px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer --></div>\n<!-- /wp:group -->\";}i:2;O:8:\"stdClass\":7:{s:2:\"id\";i:1243;s:5:\"title\";O:8:\"stdClass\":1:{s:8:\"rendered\";s:40:\"2カラムのテキストとタイトル\";}s:7:\"content\";O:8:\"stdClass\":2:{s:8:\"rendered\";s:2667:\"\n<h2 style=\"font-size:38px;line-height:1.4\"><strong>航海が始まり、柔らかな青空と穏やかな海に囲まれて幸せなスタートを切った。</strong></h2>\n\n\n\n<div class=\"wp-container-12 wp-block-columns\">\n<div class=\"wp-container-10 wp-block-column\">\n<p style=\"font-size:18px\">彼女に続いて彼らは甲板に出た。煙も家も全く見えなくなり、早朝の光に青白く照らされとても爽やかで澄んだ広い海に、船は出た。彼らはぬかるみにはまっているロンドンから抜け出した。水平線上には先細ったとても細い線の影が、パリの重荷にほとんど耐えられないほどの太さだけれど、それでも水平線上に留まっている。彼らは道路からも人間からも解放され、自分たちが自由であることに同じ爽快さをみんな感じた。</p>\n</div>\n<style>.wp-container-10 > .alignleft { float: left; margin-inline-start: 0; margin-inline-end: 2em; }.wp-container-10 > .alignright { float: right; margin-inline-start: 2em; margin-inline-end: 0; }.wp-container-10 > .aligncenter { margin-left: auto !important; margin-right: auto !important; }.wp-container-10 > * { margin-block-start: 0; margin-block-end: 0; }.wp-container-10 > * + * { margin-block-start: var( --wp--style--block-gap ); margin-block-end: 0; }</style>\n\n\n<div class=\"wp-container-11 wp-block-column\">\n<p style=\"font-size:18px\">船は小さな波の中を着実に進んでいた。波は船を叩いた後、泡立つ水のように消えていき、両側に泡と泡の小さな境界線を残した。無色透明な10月の空は、焚火の煙みたいに薄っすらと曇っていて、空気は驚くほど刺激的で爽やかだった。実にじっとしていられないほどの寒さだ。アンブローズ夫人は夫の腕を引き寄せ、二人が歩き出すと、彼女のなだらかな頬が夫に向けられたことから、彼女が何か内緒の話をしたいのだとわかった。</p>\n</div>\n<style>.wp-container-11 > .alignleft { float: left; margin-inline-start: 0; margin-inline-end: 2em; }.wp-container-11 > .alignright { float: right; margin-inline-start: 2em; margin-inline-end: 0; }.wp-container-11 > .aligncenter { margin-left: auto !important; margin-right: auto !important; }.wp-container-11 > * { margin-block-start: 0; margin-block-end: 0; }.wp-container-11 > * + * { margin-block-start: var( --wp--style--block-gap ); margin-block-end: 0; }</style></div>\n<style>.wp-container-12 {display: flex;gap: var( --wp--style--block-gap, 0.5em );flex-wrap: nowrap;align-items: center;}.wp-container-12 > * { margin: 0; }</style>\";s:9:\"protected\";b:0;}s:4:\"meta\";O:8:\