INSERT INTO `wp_postmeta` VALUES (1450,240,'interviewee','三浦宏平さん');
INSERT INTO `wp_postmeta` VALUES (1451,240,'_interviewee','field_60eb08fb03f2f');
INSERT INTO `wp_postmeta` VALUES (1452,240,'overview','当社では「WORK × LIFE SMILE ACTION～社員一人ひとりの最大活躍のために～」をテーマに、これまでも「育児、介護、治療と仕事の両立支援」を会社の重要な課題の一つとして取り組んできましたが、不妊治療の認知が世の中で高まってきた一方で、当時の会社の制度としては、不妊治療に取り組む社員のことをあまり考慮できていないという課題認識がありました。そこでまずは、不妊治療と仕事の両立に悩んでいる社員の潜在的なニーズを把握するために何か手が打てないか、と考えていたところ、御社のサービスが目に留まり、お声がけしたのがきっかけです。');
INSERT INTO `wp_postmeta` VALUES (1453,240,'_overview','field_60ec132fb3934');
